import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import AddItemButton from './AddItemButton'

const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: 200,
    },
  },
}));

class BasicTextFields extends React.Component {
  constructor(props) {
    super(props)
    this.classes = useStyles()
    this.state = {
      name: '',
      quantity: '',
      category: ''
    }
  }

  handleNameChange(event) {
    this.setState({name: event.value})
  }

  render() {
    return (<form className={this.classes.root} noValidate autoComplete="off"
    style={{
        backgroundColor: 'white',
        marginTop: '60px',
        textAlign: 'center',
        display:'flex',
        justifyContent:'center'
    }}>
      <TextField id="Name" label="Name" variant="filled" value={this.state.name} />
      <TextField id="Quantity" label="Quantity" variant="filled" value={this.state.quantity}/>
      <TextField id="Category" label="Category" variant="filled" value={this.state.category}/>
      <AddItemButton updateItems={this.props.updateItems}></AddItemButton>
    </form>
  )
  }
}

export default BasicTextFields
