import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import AddItemButton from './AddItemButton'

class AddItems extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      name: '',
      quantity: '',
      category: ''
    }

    this.handleNameChange = this.handleNameChange.bind(this)
    this.handleQuantityChange = this.handleQuantityChange.bind(this)
    this.handleCategoryChange = this.handleCategoryChange.bind(this)
  }

  handleNameChange(event) {
    console.log(value)
    this.setState({name: event.value})
  }

  handleQuantityChange(event) {
    this.setState({quantity: event.value})
  }

  handleCategoryChange(event) {
    this.setState({category: event.value})
  }

  render() {
    return (<div
    style={{
      backgroundColor: 'white',
      marginTop: '40px',
      paddingTop: '20px',
      paddingBottom: '20px'
    }}>
      <form noValidate autoComplete="off"
    style={{
        backgroundColor: 'white',
        textAlign: 'center',
        display:'flex',
        justifyContent:'center'
    }}>
      <TextField style={{marginRight: '10px'}}id="Name" label="Name" variant="filled" value={this.state.name} 
      onChange={(this.handleNameChange)}/>
      <TextField style={{marginRight: '10px'}} id="Quantity" label="Quantity" variant="filled" value={this.state.quantity} onChange={this.handleQuantityChange}/>
      <TextField style={{marginRight: '10px'}} id="Category" label="Category" variant="filled" value={this.state.category} onChange={this.handleCategoryChange}/>
      <AddItemButton 
        updateItems={this.props.updateItems}
        name={this.state.name}
        quantity={this.state.quantity}
        category={this.state.category}>
      </AddItemButton>
    </form>
    </div>
  )
  }
}

export default AddItems
