import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import AddItemButton from './AddItemButton'

const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: 200,
    },
  },
}));

class AddItems extends React.Component {
  constructor(props) {
    super(props)
    this.classes = useStyles()
    this.state = {
      name: '',
      quantity: '',
      category: ''
    }

    this.handleNameChange = this.handleNameChange.bind(this)
    this.handleQuantityChange = this.handleQuantityChange.bind(this)
    this.handleCategoryChange = this.handleCategoryChange.bind(this)
  }

  handleNameChange(event) {
    this.setState({name: event.value})
  }

  handleQuantityChange(event) {
    this.setState({quantity: event.value})
  }

  handleCategoryChange(event) {
    this.setState({category: event.value})
  }

  render() {
    return (<form noValidate autoComplete="off">
      <TextField id="Name" label="Name" variant="filled" value={this.state.name} onChange={this.handleNameChange}/>
      <TextField id="Quantity" label="Quantity" variant="filled" value={this.state.quantity} onChange={this.handleQuantityChange}/>
      <TextField id="Category" label="Category" variant="filled" value={this.state.category} onChange={this.handleCategoryChange}/>
      <AddItemButton updateItems={this.props.updateItems}
      name={this.state.name}
      quantity={this.state.quantity}
      category={this.state.category}></AddItemButton>
    </form>
  )
  }
}

export default AddItems
