import React from 'react'
import firebase from 'firebase'
import Item from './Item'
import axios from 'axios'

class OwnerList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            items: []
        }
    }
    /*
    componentDidMount() {
        axios.get(`https://us-central1-forku-dc443.cloudfunctions.net/item/get?username=${firebase.auth().currentUser.email}`)
        .then(result => {
            this.setState({items: JSON.parse(result)})
        })
    }
    */

    render() {
        let i = 0
        let itemsList = this.state.items.map(item => 
            <li key={i++}>
                <Item name={item.name} quantity={item.quantity} category={item.category}></Item>
            </li>
        )
        return (
            <ul> {itemsList} </ul>
        )
    }
}

export default OwnerList
