import React from 'react';
import './App.css';
import Login from './modules/Login'

class App extends React.Component {
  constructor() {
    this.state = {
      user: ''
    }

    this.handleLogin = this.handleLogin.bind(this)
    this.handleLogout = this.handleLogout.bind(this)
  }

  handleLogin(username) {
    this.setState({
      user: username
    })
  }

  handleLogout() {
    this.setState({
      user: ''
    })
  }

  render() {
    return (
      <div>
        <Login></Login>
      </div>
    );
  }
}

export default App;
